mapper.select:
SELECT pk_cus_sales_target_id pkCusSalesTargetId,fk_user_business_id fkUserBusinessId, cus_sales_target_year cusSalesTargetYear, sales_target_type salesTargetType,cus_sales_tar_january cusSalesTarJanuary, cus_sales_tar_february cusSalesTarFebruary,cus_sales_tar_march cusSalesTarMarch, cus_sales_tar_april cusSalesTarApril,cus_sales_tar_may cusSalesTarMay, cus_sales_tar_jun cusSalesTarJun,cus_sales_tar_july cusSalesTarJuly, cus_sales_tar_august cusSalesTarAugust,cus_sales_tar_september cusSalesTarSeptember, cus_sales_tar_october cusSalesTarOctober,cus_sales_tar_november cusSalesTarNovember, cus_sales_tar_december cusSalesTarDecember, cus_sales_tar_gmt_create cusSalesTarGmtCreate ,c.fk_user_Cre_id cusSalesTarCreator, c.cus_sales_tar_gmt_modified cusSalesTarGmtModified,c.fk_user_Upd_id cusSalesTarLastModifier, fk_user_res_id fkUserId, u.user_name userBusiness FROM cus_sales_target c,`user` u where c.fk_user_business_id = u.pk_user_id and isdel != 0
and fk_user_business_id = #{id}
and fk_user_res_id =#{id}

select count(1) from cus_sales_target where isdel != 0

    and fk_user_business_id = #{id}


    and fk_user_res_id =#{id}
